
def check_palindrome(s):
	if s == s[::-1]:
		return True
	return False

def longestPalindrome(s):
    """
    :type s: str
    :rtype: str
    """

    if len(s) == 1:
    	return s

    palindrome = ''
    for i in range(0, len(s)-1):
    	for j in range(i+1, len(s)+1):
    		word = s[i:j]

    		if check_palindrome(word) and len(word) > len(palindrome):
    			print('palindrome: {}'.format(word))
    			palindrome = word

    return palindrome


x = longestPalindrome("")
print(x)