class Solution(object):
    @staticmethod
    def print_tbl(tbl):
        for row in tbl:
            print(row)

    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        mem = [[0] * len(board[i]) for i in range(len(board))]

        self.print_tbl(board)
        print("word: %s" % word)
        
        def traverse(y, x, i):
            # y rows
            # x columns
            # ith letter in word
            if y < 0 or y > len(board) - 1:  # base case
                return False
            if x < 0 or x > len(board[y]) - 1:  # base case
                return False

            if mem[y][x] == 1:  # if coordinate is already visited
                return False

            mem[y][x] = 1

            if board[y][x] != word[i]:
                mem[y][x] = 0
                return False

            if i == len(word) - 1:
                if board[y][x] == word[i]:
                    return True
                return False

            s = traverse(y + 1, x, i + 1) or \
                traverse(y - 1, x, i + 1) or \
                traverse(y, x - 1, i + 1) or \
                traverse(y, x + 1, i + 1)

            mem[y][x] = 0 # undo the visit
            return s

        for i in range(len(board)):  # row
            for j in range(len(board[i])):  # column
                if board[i][j][0] != word[0]:
                    continue
                ans = traverse(i, j, 0)
                if ans is True:
                    print(ans)
                    print("####################\n")
                    return True
        print(False)
        print("####################\n")
        return False


